trigger.body:
	vPaymentMethod RECORD;


BEGIN

	IF UPDATING THEN
		IF (:NEW.em_sscccin_reference <> :OLD.em_sscccin_reference AND :NEW.em_sscccin_reference is not null) THEN
		UPDATE fin_payment
		SET referenceno = :NEW.em_sscccin_reference
		WHERE fin_payment_id=:old.fin_payment_id AND referenceno<>:NEW.em_sscccin_reference;
		END IF;
	END IF;

END SSCCCIN_RECONCILED_TRG
